def int addNumbers(int a, int b){
	return a+b
}
def int subtractNumbers(int a, int b){
	return a-b
}
def int divideNumbers(int a, int b){
	return a/b
}
def int multiplyNumbers(int a, int b){
	return a*b
}

int result

result = addNumbers(20,30)
log.info "addition result = $result"

result = multiplyNumbers(20,20)
log.info "multiplication result = $result"

double calculateAreaOfCircle(double radius) {
	return 3.14 * radius * radius
}
log.info "Area of Circle = " + calculateAreaOfCircle(50)
